mapper.select:
SELECT goalIdx
FROM goals
WHERE userIdx = #{userIdx} AND title=#{title} AND description=#{description}
ORDER BY createdDate DESC
LIMIT 1;

mapper.delete:
DELETE
FROM Goals
WHERE userIdx = #{userIdx} AND goalIdx = #{goalIdx}

mapper.update:
UPDATE Goals
SET title=#{title}, description=#{description}, type=#{type}, day=#{day}, startDate=#{startDate}, endDate=#{endDate}
WHERE userIdx = #{userIdx} AND goalIdx = #{goalIdx}
