mapper.delete:
DELETE
FROM goalsstatus
WHERE userIdx = #{userIdx} AND goalIdx = #{goalIdx}

mapper.update:
UPDATE goalsstatus
SET ImgUrl = #{url}
WHERE goalStatusIdx = #{goalStatusIdx}

UPDATE goalsstatus
SET time=#{time}, pillNum=#{pillNum}
WHERE goalStatusIdx=#{goalStatusIdx}

mapper.select:
SELECT COUNT(*)
FROM goalsstatus
WHERE userIdx = #{userIdx} AND goalIdx = #{goalIdx}

SELECT goalStatusIdx
FROM goalsstatus
WHERE goalIdx=#{goalIdx} AND time=#{time} AND pillNum=#{pillNum}
ORDER BY createdDate DESC
LIMIT 1;

mapper.update:
UPDATE goalsstatus
SET status=1
WHERE goalStatusIdx=#{goalStatusIdx}

UPDATE users
SET totalPoint=totalPoint+1
WHERE userIdx=#{userIdx}
