mapper.select:
select count(id)
from usersdetail
WHERE id = #{id}

mapper.update:
UPDATE users
SET name = #{name}
WHERE userIdx = #{userIdx}

mapper.select:
SELECT u.status as status, u.userIdx as userIdx
FROM usersdetail as ud
LEFT JOIN users as u ON ud.userIdx = u.userIdx
WHERE ud.id = #{id}
  AND ud.password = #{password}
  AND u.status != 'DELETE'

mapper.update:
UPDATE usersdetail
SET fcmToken = #{fcmToken}
WHERE userIdx = #{guardianIdx}

mapper.select:
SELECT ud.userIdx, u.name as name, u.profileImageUrl, u.type AS userType, ud.inviteCode, ud.phoneNumber, ud.address, ud.birth, ud.isDisease
FROM usersdetail ud
LEFT JOIN users u ON ud.userIdx = u.userIdx
WHERE ud.userIdx IN (
    SELECT seniorIdx
    FROM relations
    WHERE guardianIdx = #{userIdx}
)
AND u.status != 'DELETE'
GROUP BY ud.userIdx;

SELECT name AS diseaseName, drugName, explanation, favoriteHospital, dosageTime
FROM diseases
WHERE userIdx = #{userIdx}

SELECT seniorIdx
FROM relations
WHERE guardianIdx = #{guardianIdx}

SELECT u.name, u.profileImageUrl, ud.id, ud.phoneNumber
FROM usersdetail as ud
JOIN users as u ON ud.userIdx = u.userIdx
WHERE u.name = #{name}
  AND ud.phoneNumber = #{phoneNumber}
  AND u.status != 'DELETE'

mapper.update:
UPDATE usersdetail as ud
SET ud.password = #{newPassword}
WHERE ud.id = #{id}
  AND ud.phoneNumber = #{phoneNumber}
  AND u.status != 'DELETE'
