mapper.update:
UPDATE users
SET name = #{name}, type = #{type}
WHERE userIdx = #{userIdx}

UPDATE usersdetail
SET fcmToken = #{fcmToken}
WHERE userIdx = #{seniorIdx}

mapper.select:
SELECT fcmToken
FROM usersdetail
WHERE userIdx = #{userIdx}

mapper.update:
UPDATE relations
SET seniorIdx = #{seniorIdx}
WHERE guardianIdx = #{guardianIdx}

mapper.select:
SELECT ud.userIdx, u.name as name, u.profileImageUrl, u.type AS userType, ud.inviteCode, ud.phoneNumber, ud.address, ud.birth, ud.isDisease
FROM usersdetail ud
LEFT JOIN users u ON ud.userIdx = u.userIdx
WHERE ud.userIdx = #{userIdx}
AND u.status != 'DELETE'
GROUP BY ud.userIdx;

SELECT diseaseIdx, name AS diseaseName, drugName, explanation
FROM diseases
WHERE userIdx = #{userIdx}

SELECT guardianIdx
FROM relations
WHERE userIdx = #{userIdx}

SELECT u.userIdx, u.name, u.profileImageUrl, u.type, ud.id
FROM usersdetail as ud
JOIN users as u ON ud.userIdx = u.userIdx
WHERE ud.id = #{id}
AND u.type = 'GUARDIAN'
LIMIT 10;

mapper.update:
UPDATE diseases
SET name = #{disease.name}, drugName = #{disease.drugName}, explanation = #{disease.explanation}
WHERE diseaseIdx = #{disease.diseaseIdx}
AND userIdx = #{userIdx};
